import React, { Component } from "react";
import io from "socket.io-client";
import axios from "axios";
import { Route } from "react-router-dom";

import HomePage from "./componets/homePage";

import LeftNavbar from "./componets/navbar/leftNavbar";
import TopNavbar from "./componets/navbar/topNavbar";

import ChatPage from "./componets/chat/chatPage";

import SignupPage from "./componets/user/signupPage";
import LoginPage from "./componets/user/loginPage";

import ThrowFlash from "./componets/util/throwFlash";

class App extends Component {
  constructor() {
    super();
    this.socket = io.connect("/");
    this.state = {
      loggedIn: false,
      username: null,
      chatroom: null,
      id: null,
      flashes: [],
    };
    this.updateAppState = this.updateAppState.bind(this);
    this.logout = this.logout.bind(this);
    this.login = this.login.bind(this);
  }
  componentDidMount() {
    this.socket.on("init", (data) => {
      console.log(data);
    });
  }
  updateAppState(stateObject) {
    this.setState(stateObject);
    console.log(this.state);
  }
  logout() {
    axios
      .post("/user/logout")
      .then((response) => {
        //sends a post request to log user out of current session
        if (response.status === 200) {
          this.setState({
            loggedIn: false,
            username: null,
          });
          window.location.pathname = "/";
        }
      })
      .catch((error) => {
        console.log("Logout error", error);
      });
  }
  login(username, password) {
    axios.post("/user/login", { username, password }).then((response) => {
      if (response.status === 200) {
        if (response.data.error) {
          let msg = response.data.error.msg;
          this.addFlash({ success: false, msg });
        } else {
          console.log(response.data);
          this.setState({
            loggedIn: true,
            username: this.response.data.username,
          });
        }
      }
    });
  }
  signup(username, email, password, confirmPassword) {
    axios
      .post("/user/", { username, email, password, confirmPassword })
      .then((response) => {
        if (response.data.error) {
          let msg = response.data.error.msg;
          this.addFlash({ success: false, msg });
        } else {
          console.log(response.data);
          this.addFlash({ success: true, msg: "Successfuly created account" });
        }
      })
      .catch((error) => {
        console.log(error);
        this.addFlash({
          success: false,
          msg: "An unexpected error has occurred. Please try again later",
        });
      });
  }
  addFlash(flash) {
    let flashes = this.state.flashes;
    flashes.push(flash);
    this.setState({ flashes });
  }

  renderFlash() {
    return this.state.flashes.map(({ msg, success }, index) => {
      return (
        <ThrowFlash
          updateAppState={this.updateAppState}
          msg={msg}
          success={success}
          index={index}
          flashes={this.state.flashes}
          key={index}
        />
      );
    });
  }

  render() {
    return (
      <div>
        <TopNavbar logout={this.logout} />
        <LeftNavbar updateAppState={this.updateAppState} socket={this.socket} />
        <Route path="/" exact component={HomePage} />
        <div className="container mt-5 ">
          {this.renderFlash()}
          <Route
            path="/chat/"
            exact
            render={() => (
              <ChatPage
                updateAppState={this.updateAppState}
                id={this.state.id}
                socket={this.socket}
                username={this.state.username}
              />
            )}
          />
          <Route
            path="/login"
            render={() => (
              <LoginPage login={this.login} loggedIn={this.state.loggedIn} />
            )}
          />
          <Route
            path="/signup"
            render={() => <SignupPage addFlash={this.addFlash} />}
          />
        </div>
      </div>
    );
  }
}

export default App;
