import React, { Component } from "react";
import ChatLogs from "./chatLogs";
import { Redirect } from "react-router-dom";
class ChatPage extends Component {
  _isMounted = false;
  constructor(props) {
    super(props);
    this.state = {
      msg: "",
      chat: [],
      redirectTo: null,
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  componentDidMount() {
    this._isMounted = true;
    this.props.socket.emit("connect_to", { socket: this.props.id });
    this.props.socket.on("connect_to", (data) => {
      //this.setState({ chat: data });
      if (data.error) {
        this.props.addFlash({ success: false, msg: data.error.msg });
        this.setState({ redirectTo: "/" });
      } else {
        console.log(data);
      }
    });
    this.props.socket.on("chat_message", (data) => {
      this.setState({
        chat: [...this.state.chat, { user: data.user, msg: data.msg }],
      });
      if (this.props.username.user === data.user) {
        this.chatbox.scrollTo(0, this.chatbox.scrollHeight);
      }
    });
  }
  componentWillUnmount() {
    this._isMounted = false;
  }
  onTextChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit(event) {
    event.preventDefault();
    console.log("hello");
    /*
    this.props.socket.emit("chat_message", {
      user: this.props.username,
      msg: this.state.msg,
    });
    this.setState({ msg: "" });
    */
  }

  renderChat() {
    const { chat } = this.state;
    return chat.map(({ user, msg }, idx) => {
      if (user === this.props.username) {
        return <ChatLogs tf={true} key={idx} msg={msg} user={user} />;
      } else {
        return <ChatLogs tf={false} key={idx} msg={msg} user={user} />;
      }
    });
  }

  render() {
    if (this.state.redirectTo) {
      return <Redirect to={{ pathname: this.state.redirectTo }} />;
    } else {
      return (
        <div className="background ss">
          <div className="card card-body cscard chat">
            <div className="chatbox ss3" ref={(c) => (this.chatbox = c)}>
              {this.renderChat()}
            </div>
            <form onSubmit={this.handleSubmit} className="input-group mb-3">
              <div className="input-group-prepend">
                <button className="btn text-left  csnav-btn " type="submit">
                  Send
                </button>
              </div>
              <input type="text" className="form-control" placeholder="" />
            </form>
          </div>
        </div>
      );
    }
  }
}

export default ChatPage;
