import React, { Component } from "react";
import axios from "axios";
import io from "socket.io-client";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import HomePage from "./componets/homePage";

const server = "http://localhost:5050";
const socket = io.connect(server);
let current_user;
if (JSON.parse(window.sessionStorage.getItem("user"))) {
  current_user = JSON.parse(window.sessionStorage.getItem("user"))[0];
} else {
  current_user = { username: "" };
}
class App extends React.Component {
  render() {
    return (
      <Router>
        <Route path="/" exact component={HomePage} />
        <Route path="/chat/:chatype/:chatroom/" exact component={ChatPage} />
        <Route path="/account/signin" exact component={SignInPage} />
        <Route path="/account/signup" exact component={SignUpPage} />
      </Router>
    );
  }
}

class HomePage2 extends React.Component {
  constructor(props) {
    super(props);
    this._isMounted = false;
  }
  componentDidMount() {
    this._isMounted = true;
  }
  componentWillUnmount() {
    this._isMounted = false;
  }
  render() {
    return (
      <div>
        <Navbar chatype={null} />
        <div className="backgoundpage chatpage"></div>
      </div>
    );
  }
}

class ChatPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      msg: "",
      chat: [],
      user: current_user.username,
      prevchatroom: this.props.match.params.chatroom,
    };
    this._isMounted = false;
  }
  componentDidMount() {
    this._isMounted = true;
    socket.emit("connect_to", {
      socket: this.props.match.params.chatroom,
      type: this.props.match.params.chatype,
    });
    socket.on("connect_to", (data) => {
      this.setState({ chat: data });
    });
    socket.on("chat_message", (data) => {
      this.setState({
        chat: [...this.state.chat, { user: data.user, msg: data.msg }],
      });
      if (this.state.user === data.user) {
        this.chatbox.scrollTo(0, this.chatbox.scrollHeight);
      }
    });
  }
  componentDidUpdate() {
    if (this.state.prevchatroom !== this.props.match.params.chatroom) {
      this.setState({ prevchatroom: this.props.match.params.chatroom });
      socket.emit("connect_to", {
        socket: this.props.match.params.chatroom,
        type: this.props.match.params.chatype,
      });
    }
  }
  componentWillUnmount() {
    this._isMounted = false;
  }
  onTextChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onMessageSubmit = (e) => {
    e.preventDefault();
    socket.emit("chat_message", {
      user: this.state.user,
      msg: this.state.msg,
      type: this.props.match.params.chatype,
    });
    this.setState({ msg: "" });
  };

  renderChat() {
    const { chat } = this.state;
    return chat.map(({ user, msg }, idx) => {
      if (user === current_user.username) {
        return <ChatLogs tf={true} key={idx} msg={msg} user={user} />;
      } else {
        return <ChatLogs tf={false} key={idx} msg={msg} user={user} />;
      }
    });
  }

  render() {
    return (
      <div>
        <div className="chatbackground chatpage">
          <div className="render-chat">
            <div className="chatbox" ref={(c) => (this.chatbox = c)}>
              {this.renderChat()}
            </div>
          </div>
          <form
            onSubmit={this.onMessageSubmit}
            className=" input-group messageform "
          >
            <div className="input-group-prepend">
              <button
                type="button"
                className="btn text-left text-info  input-group-prepend"
                onClick={this.onMessageSubmit}
              >
                Send
              </button>
            </div>
            <input
              name="msg"
              className="form-control "
              onChange={(e) => this.onTextChange(e)}
              value={this.state.msg}
              placeholder="message"
            />
          </form>
        </div>
      </div>
    );
  }
}
/*
<Navbar username={this.props.match.params.user} />
*/
class ChatLogs extends React.Component {
  render() {
    return (
      <div className={`${this.props.tf ? "text-right" : "text-left"} m-2`}>
        <span className="badge badge-dark">
          <h6 className="m-0 text-info">{this.props.msg}</h6>
        </span>
      </div>
    );
  }
}

class NavbarDirectItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = { touser: null };
    this._isMounted = false;
  }
  componentDidMount() {
    this._isMounted = true;
    const prom = new Promise((resolve) => {
      let dmuser = this.props.tag.filter(
        (word) => word !== current_user.username
      );
      resolve(dmuser);
    });
    prom.then((data) => {
      this.setState({ touser: data });
    });
  }
  componentWillUnmount() {
    this._isMounted = false;
  }
  render() {
    return (
      <Link to={`/chat/${this.props.chatype}/${this.props.direct._id}/`}>
        <button className="btn text-left text-info nav-btn mb-2">
          <h5>
            To
            <svg
              width="1em"
              height="1em"
              viewBox="0 0 16 16"
              className="bi bi-forward-fill mr-1 ml-1"
              fill="currentColor"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path d="M9.77 12.11l4.012-2.953a.647.647 0 0 0 0-1.114L9.771 5.09a.644.644 0 0 0-.971.557V6.65H2v3.9h6.8v1.003c0 .505.545.808.97.557z" />
            </svg>
            {this.state.touser}
          </h5>
        </button>
      </Link>
    );
  }
}
class SignInPage extends React.Component {
  constructor(props) {
    super(props);
    this._isMounted = false;
    this.state = { user: null, enabled: false };
    this.loginintoaccount = this.loginintoaccount.bind(this);
  }
  componentDidMount() {
    this._isMounted = true;
    socket.on("signin_user", (data) => {
      if (!window.sessionStorage.getItem("user")) {
        window.sessionStorage.setItem("user", JSON.stringify(data));
        current_user = JSON.parse(window.sessionStorage.getItem("user"))[0];
      } else {
        window.sessionStorage.removeItem("user");
        window.sessionStorage.setItem("user", JSON.stringify(data));
        current_user = JSON.parse(window.sessionStorage.getItem("user"))[0];
      }
      this.setState({ enabled: true });
    });
    socket.on("sign_in_error", () => {
      alert("that account doesnt exist");
    });
  }
  componentWillUnmount() {
    this._isMounted = false;
  }
  loginintoaccount(user, password) {
    socket.emit("signin_user", { user: user, password: password });
  }
  render() {
    return (
      <div className="backgoundpage form-page">
        <div className="form-box">
          <FormGroup
            submit={this.loginintoaccount}
            enabled={this.state.enabled}
          />
        </div>
      </div>
    );
  }
}
class SignUpPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = { enabled: false };
    this._isMounted = false;
    this.createnewuser = this.createnewuser.bind(this);
    this.isValid = this.isValid.bind(this);
    this.checkstate = this.checkstate.bind(this);
  }

  componentDidMount() {
    this._isMounted = true;
    socket.on("create_user", (data) => {
      if (!window.sessionStorage.getItem("user")) {
        window.sessionStorage.setItem("user", JSON.stringify(data));
        current_user = JSON.parse(window.sessionStorage.getItem("user"));
      } else {
        window.sessionStorage.removeItem("user");
        window.sessionStorage.setItem("user", JSON.stringify(data));
        current_user = JSON.parse(window.sessionStorage.getItem("user"));
      }
      this.setState({ enabled: true });
    });
    socket.on("create_user_faild", () => {
      alert("account already exists");
    });
  }
  componentWillUnmount() {
    this._isMounted = false;
  }
  isValid(str) {
    if (/^[a-zA-Z0-9- ,_]*$/.test(str) === false) {
      return false;
    } else {
      return true;
    }
  }
  checkstate(username, password) {
    if (!username || !password) {
      return false;
    } else {
      if (this.isValid(username)) {
        return true;
      } else {
        return false;
      }
    }
  }
  createnewuser(username, password) {
    if (this.checkstate(username, password)) {
      socket.emit("create_user", { username, password });
    }
  }

  render() {
    return (
      <div className="backgoundpage form-page">
        <div className="form-box">
          <FormGroup submit={this.createnewuser} enabled={this.state.enabled} />
        </div>
      </div>
    );
  }
}

class FormGroup extends React.Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }
  handleClick() {
    let username = this.usernamebox.value;
    let password = this.passwordbox.value;
    this.props.submit(username, password);
  }

  render() {
    return (
      <form className="m-5">
        <div className="row">
          <input
            type="text"
            className="form-control form-control-lg"
            ref={(c) => (this.usernamebox = c)}
            placeholder="Username"
            autoComplete="username"
          />
        </div>
        <div className="row">
          <input
            type="password"
            className="form-control form-control-lg"
            ref={(c) => (this.passwordbox = c)}
            placeholder="Password"
            autoComplete="password"
          />
        </div>
        <div className="row">
          <Link to="/">
            <button type="button" className="btn mt-2 mr-1 btn-sm text-info">
              <div className="row m-1">
                <h5>Back</h5>
                <svg
                  width="1em"
                  height="1em"
                  viewBox="0 0 16 16"
                  className="bi bi-box-arrow-in-left"
                  fill="currentColor"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    fillRule="evenodd"
                    d="M7.854 11.354a.5.5 0 0 0 0-.708L5.207 8l2.647-2.646a.5.5 0 1 0-.708-.708l-3 3a.5.5 0 0 0 0 .708l3 3a.5.5 0 0 0 .708 0z"
                  />
                  <path
                    fillRule="evenodd"
                    d="M15 8a.5.5 0 0 0-.5-.5h-9a.5.5 0 0 0 0 1h9A.5.5 0 0 0 15 8z"
                  />
                  <path
                    fillRule="evenodd"
                    d="M2.5 14.5A1.5 1.5 0 0 1 1 13V3a1.5 1.5 0 0 1 1.5-1.5h8A1.5 1.5 0 0 1 12 3v1.5a.5.5 0 0 1-1 0V3a.5.5 0 0 0-.5-.5h-8A.5.5 0 0 0 2 3v10a.5.5 0 0 0 .5.5h8a.5.5 0 0 0 .5-.5v-1.5a.5.5 0 0 1 1 0V13a1.5 1.5 0 0 1-1.5 1.5h-8z"
                  />
                </svg>
              </div>
            </button>
          </Link>
          <button
            type="button"
            className="btn mt-2 btn-sm text-info"
            onClick={this.handleClick}
          >
            <div className="row m-1">
              <h5>submit</h5>
              <svg
                width="1em"
                height="1em"
                viewBox="0 0 16 16"
                className="bi bi-box-arrow-in-up"
                fill="currentColor"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  fillRule="evenodd"
                  d="M4.646 7.854a.5.5 0 0 0 .708 0L8 5.207l2.646 2.647a.5.5 0 0 0 .708-.708l-3-3a.5.5 0 0 0-.708 0l-3 3a.5.5 0 0 0 0 .708z"
                />
                <path
                  fillRule="evenodd"
                  d="M8 15a.5.5 0 0 0 .5-.5v-9a.5.5 0 0 0-1 0v9a.5.5 0 0 0 .5.5z"
                />
                <path
                  fillRule="evenodd"
                  d="M1.5 2.5A1.5 1.5 0 0 1 3 1h10a1.5 1.5 0 0 1 1.5 1.5v8A1.5 1.5 0 0 1 13 12h-1.5a.5.5 0 0 1 0-1H13a.5.5 0 0 0 .5-.5v-8A.5.5 0 0 0 13 2H3a.5.5 0 0 0-.5.5v8a.5.5 0 0 0 .5.5h1.5a.5.5 0 0 1 0 1H3a1.5 1.5 0 0 1-1.5-1.5v-8z"
                />
              </svg>
            </div>
          </button>
          {this.props.enabled ? (
            <Link to={`/home/`}>
              <button
                type="button"
                className={`btn mt-2 ml-1  btn-sm ${
                  this.props.enabled ? "text-info" : "disabled"
                }`}
              >
                <div className="row m-1">
                  <h5>continue</h5>
                  <svg
                    width="1em"
                    height="1em"
                    viewBox="0 0 16 16"
                    className="bi bi-box-arrow-in-right"
                    fill="currentColor"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path
                      fillRule="evenodd"
                      d="M8.146 11.354a.5.5 0 0 1 0-.708L10.793 8 8.146 5.354a.5.5 0 1 1 .708-.708l3 3a.5.5 0 0 1 0 .708l-3 3a.5.5 0 0 1-.708 0z"
                    />
                    <path
                      fillRule="evenodd"
                      d="M1 8a.5.5 0 0 1 .5-.5h9a.5.5 0 0 1 0 1h-9A.5.5 0 0 1 1 8z"
                    />
                    <path
                      fillRule="evenodd"
                      d="M13.5 14.5A1.5 1.5 0 0 0 15 13V3a1.5 1.5 0 0 0-1.5-1.5h-8A1.5 1.5 0 0 0 4 3v1.5a.5.5 0 0 0 1 0V3a.5.5 0 0 1 .5-.5h8a.5.5 0 0 1 .5.5v10a.5.5 0 0 1-.5.5h-8A.5.5 0 0 1 5 13v-1.5a.5.5 0 0 0-1 0V13a1.5 1.5 0 0 0 1.5 1.5h8z"
