import React, { Component } from "react";

class ChatPage extends Component {
  _isMounted = false;
  constructor(props) {
    super(props);
    this.state = {
      msg: "",
      chat: [],
      user: current_user.username,
      prevchatroom: this.props.match.params.chatroom,
    };
  }
  componentDidMount() {
    this._isMounted = true;
    socket.emit("connect_to", {
      socket: this.props.match.params.chatroom,
      type: this.props.match.params.chatype,
    });
    socket.on("connect_to", (data) => {
      this.setState({ chat: data });
    });
    socket.on("chat_message", (data) => {
      this.setState({
        chat: [...this.state.chat, { user: data.user, msg: data.msg }],
      });
      if (this.state.user === data.user) {
        this.chatbox.scrollTo(0, this.chatbox.scrollHeight);
      }
    });
  }
  componentDidUpdate() {
    if (this.state.prevchatroom !== this.props.match.params.chatroom) {
      this.setState({ prevchatroom: this.props.match.params.chatroom });
      socket.emit("connect_to", {
        socket: this.props.match.params.chatroom,
        type: this.props.match.params.chatype,
      });
    }
  }
  componentWillUnmount() {
    this._isMounted = false;
  }
  onTextChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onMessageSubmit = (e) => {
    e.preventDefault();
    socket.emit("chat_message", {
      user: this.state.user,
      msg: this.state.msg,
      type: this.props.match.params.chatype,
    });
    this.setState({ msg: "" });
  };

  renderChat() {
    const { chat } = this.state;
    return chat.map(({ user, msg }, idx) => {
      if (user === current_user.username) {
        return <ChatLogs tf={true} key={idx} msg={msg} user={user} />;
      } else {
        return <ChatLogs tf={false} key={idx} msg={msg} user={user} />;
      }
    });
  }

  render() {
    return (
      <div>
        <div className="chatbackground chatpage">
          <div className="render-chat">
            <div className="chatbox" ref={(c) => (this.chatbox = c)}>
              {this.renderChat()}
            </div>
          </div>
          <form
            onSubmit={this.onMessageSubmit}
            className=" input-group messageform "
          >
            <div className="input-group-prepend">
              <button
                type="button"
                className="btn text-left text-info  input-group-prepend"
                onClick={this.onMessageSubmit}
              >
                Send
              </button>
            </div>
            <input
              name="msg"
              className="form-control "
              onChange={(e) => this.onTextChange(e)}
              value={this.state.msg}
              placeholder="message"
            />
          </form>
        </div>
      </div>
    );
  }
}

export default ChatPage;
