import React, { Component } from "react";
import { Link } from "react-router-dom";
class ChatroomLink extends Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }
  handleClick() {
    this.props.onClick(this.props.chatroom._id);
  }
  render() {
    return (
      <button className="btn text-left text-info nav-btn mb-2">
        <h5>{this.props.chatroom.chatroom}</h5>
      </button>
    );
  }
}

export default ChatroomLink;
