import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import DirectLink from "./links/directLink";
import ChatroomLink from "./links/chatroomLink";
class Navbar extends Component {
  constructor(props) {
    super(props);
    this._isMounted = false;
    this.displaychatroomitems = this.displaychatroomitems.bind(this);
    this.displaydirectitems = this.displaydirectitems.bind(this);

    this.state = {
      chatrooms: [],
      chatroom: null,
      directData: [],
      directto: null,
    };
    this.source = axios.CancelToken.source();
    this.getChatrooms = this.getChatrooms.bind(this);
    this.getDirect = this.getDirect.bind(this);
  }
  componentDidMount() {
    this._isMounted = true;
    this.props.socket.on("create_chat", () => {
      this.getChatrooms();
    });
    this.props.socket.on("create_direct", () => {
      this.getDirect();
    });
    this.props.socket.on("get_chat", (data) => {
      this.setState({ chatrooms: data });
    });
    this.props.socket.on("get_direct", (data) => {
      this.setState({ directData: data });
    });

    this.getChatrooms();
    this.getDirect();
  }
  componentWillUnmount() {
    this._isMounted = false;
    this.source.cancel();
  }

  getChatrooms() {
    this.props.socket.emit("get_chat");
  }
  getDirect() {
    this.props.socket.emit("get_direct");
  }
  displaychatroomitems() {
    return this.state.chatrooms.map((i) => {
      return (
        <ChatroomLink
          chatroom={i}
          username={this.props.username}
          key={i.chatroom}
          chatype={"chatrooms"}
        />
      );
    });
  }
  displaydirectitems() {
    return this.state.directData.map((i, index) => {
      return (
        <DirectLink direct={i} tag={i.users} key={index} chatype={"direct"} />
      );
    });
  }
  render() {
    return (
      <div className="nav bg-dark">
        <div className="col">
          <div className="d-block">
            <div className="bg-dark card mb-2">
              <h2 className="mb-0">
                <button className="btn text-left  nav-btn " type="button">
                  <Link to="/home/">
                    <h5 className="text-info">
                      Home
                      <svg
                        width="1em"
                        height="1em"
                        viewBox="0 0 16 16"
                        className="bi bi-house ml-1"
                        fill="currentColor"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          fillRule="evenodd"
                          d="M2 13.5V7h1v6.5a.5.5 0 0 0 .5.5h9a.5.5 0 0 0 .5-.5V7h1v6.5a1.5 1.5 0 0 1-1.5 1.5h-9A1.5 1.5 0 0 1 2 13.5zm11-11V6l-2-2V2.5a.5.5 0 0 1 .5-.5h1a.5.5 0 0 1 .5.5z"
                        />
                        <path
                          fillRule="evenodd"
                          d="M7.293 1.5a1 1 0 0 1 1.414 0l6.647 6.646a.5.5 0 0 1-.708.708L8 2.207 1.354 8.854a.5.5 0 1 1-.708-.708L7.293 1.5z"
                        />
                      </svg>
                    </h5>
                  </Link>
                </button>
              </h2>
            </div>
          </div>
          <div className="d-block">
            <div className="bg-dark card mb-2">
              <h2 className="mb-0">
                <button className="btn text-left  nav-btn " type="button">
                  <h5 className="text-info">
                    New Chatroom
                    <svg
                      width="1em"
                      height="1em"
                      viewBox="0 0 16 16"
                      className="bi bi-chat-left-quote ml-1"
                      fill="currentColor"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        fillRule="evenodd"
                        d="M14 1H2a1 1 0 0 0-1 1v11.586l2-2A2 2 0 0 1 4.414 11H14a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1zM2 0a2 2 0 0 0-2 2v12.793a.5.5 0 0 0 .854.353l2.853-2.853A1 1 0 0 1 4.414 12H14a2 2 0 0 0 2-2V2a2 2 0 0 0-2-2H2z"
                      />
                      <path
                        fillRule="evenodd"
                        d="M7.066 4.76A1.665 1.665 0 0 0 4 5.668a1.667 1.667 0 0 0 2.561 1.406c-.131.389-.375.804-.777 1.22a.417.417 0 1 0 .6.58c1.486-1.54 1.293-3.214.682-4.112zm4 0A1.665 1.665 0 0 0 8 5.668a1.667 1.667 0 0 0 2.561 1.406c-.131.389-.375.804-.777 1.22a.417.417 0 1 0 .6.58c1.486-1.54 1.293-3.214.682-4.112z"
                      />
                    </svg>
                  </h5>
                </button>
              </h2>
            </div>
          </div>
          <div className="d-block">
            <div className="bg-dark card mb-2">
              <h2 className="mb-0">
                <button className="btn text-left  nav-btn " type="button">
                  <h5 className="text-info">
                    New Direct
                    <svg
                      width="1em"
                      height="1em"
                      viewBox="0 0 16 16"
                      className="bi bi-cursor ml-1"
                      fill="currentColor"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        fillRule="evenodd"
                        d="M14.082 2.182a.5.5 0 0 1 .103.557L8.528 15.467a.5.5 0 0 1-.917-.007L5.57 10.694.803 8.652a.5.5 0 0 1-.006-.916l12.728-5.657a.5.5 0 0 1 .556.103zM2.25 8.184l3.897 1.67a.5.5 0 0 1 .262.263l1.67 3.897L12.743 3.52 2.25 8.184z"
                      />
                    </svg>
                  </h5>
                </button>
              </h2>
            </div>
          </div>
          <h4>
            Chatrooms
            <svg
              width="1em"
              height="1em"
              viewBox="0 0 16 16"
              className="bi bi-chat-left ml-1"
              fill="currentColor"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                fillRule="evenodd"
                d="M14 1H2a1 1 0 0 0-1 1v11.586l2-2A2 2 0 0 1 4.414 11H14a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1zM2 0a2 2 0 0 0-2 2v12.793a.5.5 0 0 0 .854.353l2.853-2.853A1 1 0 0 1 4.414 12H14a2 2 0 0 0 2-2V2a2 2 0 0 0-2-2H2z"
              />
            </svg>
          </h4>
          <div className="navBox">{this.displaychatroomitems()}</div>
          <h4>
            Directs
            <svg
              width="1em"
              height="1em"
              viewBox="0 0 16 16"
              className="bi bi-cast ml-1"
              fill="currentColor"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path d="M7.646 9.354l-3.792 3.792a.5.5 0 0 0 .353.854h7.586a.5.5 0 0 0 .354-.854L8.354 9.354a.5.5 0 0 0-.708 0z" />
              <path d="M11.414 11H14.5a.5.5 0 0 0 .5-.5v-7a.5.5 0 0 0-.5-.5h-13a.5.5 0 0 0-.5.5v7a.5.5 0 0 0 .5.5h3.086l-1 1H1.5A1.5 1.5 0 0 1 0 10.5v-7A1.5 1.5 0 0 1 1.5 2h13A1.5 1.5 0 0 1 16 3.5v7a1.5 1.5 0 0 1-1.5 1.5h-2.086l-1-1z" />
            </svg>
          </h4>
          <div className="navBox mt-4">{this.displaydirectitems()}</div>
        </div>
      </div>
    );
  }
}

export default Navbar;
