import React, { Component } from "react";
import io from "socket.io-client";
import axios from "axios";
import { Route } from "react-router-dom";
import HomePage from "./componets/homePage";

import LeftNavbar from "./componets/navbar/leftNavbar";
import TopNavbar from "./componets/navbar/topNavbar";

import ChatPage from "./componets/chat/chatPage";

import SignupPage from "./componets/user/signupPage";
import LoginPage from "./componets/user/loginPage";

import ThrowFlash from "./componets/util/throwFlash";

class App extends Component {
  _isMounted = false;
  constructor() {
    super();
    this.socket = io.connect("/");
    this.state = {
      loggedIn: false,
      username: null,
      userid: null,
      chatroom: null,
      id: null,
      flashes: [],
      redirectTo: null,
    };
    this.updateAppState = this.updateAppState.bind(this);
    this.getUser = this.getUser.bind(this);
    this.logout = this.logout.bind(this);
    this.login = this.login.bind(this);
    this.signup = this.signup.bind(this);
    this.addFlash = this.addFlash.bind(this);
    this.removeFlash = this.removeFlash.bind(this);
    this.setRedirect = this.setRedirect.bind(this);
  }
  componentDidMount() {
    this._isMounted = true;
    this.setState({ redirectTo: null });
    this.socket.on("init", (data) => {
      console.log(data);
    });
    this.getUser();
  }
  componentDidUpdate() {
    this._isMounted = false;
  }
  updateAppState(stateObject) {
    this.setState(stateObject);
    console.log(this.state);
  }
  createChat(name) {
    axios.post("/create/chatroom", {
      name,
      owner: { id: this.state.userid, username: this.state.username },
    });
  }
  getUser() {
    axios.get("/user/").then((response) => {
      if (response.data.user) {
        // if express sees that there is a authenicated
        // user saved in the session it will return the users data
        this.setState({
          loggedIn: true,
          username: response.data.user.username,
          userid: response.data.user.id,
        });
      } else {
        //if no user is saved in the express session it will simply go about it's day
        console.log("Get user: no user");
        this.setState({
          loggedIn: false,
          username: null,
        });
      }
    });
  }
  logout() {
    axios
      .post("/user/logout")
      .then((response) => {
        //sends a post request to log user out of current session
        if (response.status === 200) {
          this.addFlash({ success: true, msg: "Successfully logged out" });
          this.setState({
            loggedIn: false,
            username: null,
          });
        }
      })
      .catch((error) => {
        console.log("Logout error", error);
      });
  }
  login(username, password) {
    axios
      .post("/user/login", { username, password })
      .then((response) => {
        if (response.status === 200) {
          if (response.data.error) {
            let msg = response.data.error.msg;
            this.addFlash({ success: false, msg });
          } else {
            console.log(response.data);
            this.setState({
              loggedIn: true,
              username: response.data.username,
            });
            this.addFlash({ success: true, msg: "Logged in successfully " });
            this.setRedirect("/");
          }
        }
      })
      .catch((error) => {
        console.log(error);
        this.addFlash({
          success: false,
          msg: "An unexpected error has occurred. Please try again later",
        });
      });
  }
  signup(username, email, password, confirmPassword) {
    axios
      .post("/user/", { username, email, password, confirmPassword })
      .then((response) => {
        if (response.data.error) {
          let msg = response.data.error.msg;
          this.addFlash({ success: false, msg });
        } else {
          console.log(response.data);
          this.addFlash({ success: true, msg: "Successfully created account" });
          this.setRedirect("/login");
        }
      })
      .catch((error) => {
        console.log(error);
        this.addFlash({
          success: false,
          msg: "An unexpected error has occurred. Please try again later",
        });
      });
  }
  addFlash(flash) {
    let flashes = this.state.flashes;
    flashes.push(flash);
    this.setState({ flashes });
  }
  removeFlash(index) {
    let flashes = this.state.flashes;
    flashes.splice(index, 1);
    this.setState({ flashes });
  }

  setRedirect(redirectTo) {
    this.setState({ redirectTo });
  }
  renderFlash() {
    return this.state.flashes.map(({ msg, success }, index) => {
      return (
        <ThrowFlash
          removeFlash={this.removeFlash}
          msg={msg}
          success={success}
          index={index}
          key={index}
          length={this.state.flashes.length}
        />
      );
    });
  }

  render() {
    return (
      <div>
        <TopNavbar logout={this.logout} loggedIn={this.state.loggedIn} />
        <LeftNavbar updateAppState={this.updateAppState} socket={this.socket} />
        <div className="mt-5 ">
          {this.renderFlash()}
          <Route
            path="/"
            exact
            render={() => (
              <HomePage
                user={this.state.username}
                loggedIn={this.state.loggedIn}
              />
            )}
          />
          <Route
            path="/chat/"
            render={() => (
              <ChatPage
                updateAppState={this.updateAppState}
                addFlash={this.addFlash}
                id={this.state.id}
                socket={this.socket}
                username={this.state.username}
              />
            )}
          />
          <Route
            path="/login"
            render={() => (
              <LoginPage
                login={this.login}
                loggedIn={this.state.loggedIn}
                redirectTo={this.state.redirectTo}
                setRedirect={this.setRedirect}
              />
            )}
          />
          <Route
            path="/signup"
            render={() => (
              <SignupPage
                signup={this.signup}
                redirectTo={this.state.redirectTo}
                setRedirect={this.setRedirect}
              />
            )}
          />
        </div>
      </div>
    );
  }
}

export default App;
