import React, { Component } from "react";
import io from "socket.io-client";
import { Route } from "react-router-dom";

import HomePage from "./componets/homePage";
import LeftNavbar from "./componets/navbar/leftNavbar";
import TopNavbar from "./componets/navbar/topNavbar";
import ChatPage from "./componets/chat/chatPage";
class App extends Component {
  constructor() {
    super();
    this.socket = io.connect("/");
    this.state = {
      loggedIn: false,
      username: null,
      chatroom: null,
      id: null,
    };
    this.updateAppState = this.updateAppState.bind(this);
  }
  componentDidMount() {
    this.socket.on("init", (data) => {
      console.log(data);
    });
  }
  updateAppState(stateObject) {
    this.setState(stateObject);
    console.log(this.state);
  }
  render() {
    return (
      <div>
        <TopNavbar updateAppState={this.updateAppState} />
        <LeftNavbar updateAppState={this.updateAppState} socket={this.socket} />
        <Route path="/" exact component={HomePage} />
        <Route
          path="/chat/"
          exact
          render={() => (
            <ChatPage
              updateAppState={this.updateAppState}
              id={this.state.id}
              socket={this.socket}
              username={this.state.username}
            />
          )}
        />
      </div>
    );
  }
}

export default App;
