import React, { Component } from "react";

class Dialog extends Component {
  constructor(props) {
    super(props);
    this.state = { isDialog: false, name: "" };
    this.handleClick = this.handleClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.overLayRef = React.createRef();
  }
  componentDidMount() {
    console.log(this.props);
  }
  componentDidUpdate() {
    const overlay = this.overLayRef.current;
    if (this.props.isDialog !== this.state.isDialog) {
      let isDialog = this.props.isDialog;
      this.setState({ isDialog });
      overlay.classList.toggle("show");
    }
  }
  handleClick() {
    let isDialog = this.props.isDialog;
    isDialog = !isDialog;
    this.props.updateAppState({ isDialog });
    console.log(isDialog);
  }
  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }
  render() {
    return (
      <div>
        {this.props.isDialog ? (
          <div className="dialog bg-dark col-md-6 m-auto">
            <button onClick={this.handleClick} className="dialogBtn bg-dark">
              &times;
            </button>
            <div>
              <div className="dialogText">{this.props.children}</div>
              <form className="form-group">
                <input
                  type="text"
                  name="name"
                  onChange={(e) => this.handleChange(e)}
                  value={this.state.name}
                  className="form-control"
                  placeholder="Enter Name"
                />
              </form>
            </div>
            <div className="dialogOptions">
              <button onClick={this.handleClick} className="btn btn-danger">
                Cancel
              </button>
              <button className="btn btn-success">Confirm</button>
            </div>
          </div>
        ) : (
          ""
        )}
        <div
          onClick={this.handleClick}
          ref={this.overLayRef}
          className="overlay"
        ></div>
      </div>
    );
  }
}

export default Dialog;
